function toggleNav(){console.log("toggle");var e=document.querySelector("nav ul"),o=document.querySelector(".mobile-menu-btn i:nth-child(1)"),n=document.querySelector(".mobile-menu-btn i:nth-child(2)");o.classList.toggle("hidden"),n.classList.toggle("hidden"),e.classList.toggle("visible")}function addPotion(e){var o=document.createElement("div"),n='<div><span class="potion-name">'+e.name+'</span><span class="potion-price">'+e.price+"</span></div>";o.innerHTML=n,document.querySelector(".l-potions").appendChild(o.firstChild)}window.onload=function(){var n=new XMLHttpRequest;n.open("GET","../data/potions.json",!0),n.responseType="json",n.onload=function(){if(200===n.status)for(var e=n.response.potions,o=0;o<e;o++)addPotion(e[o]);else console.error("Error getting potions JSON.")},n.send()};